import React, { useState } from 'react'
import { Container, Form, Button } from 'react-bootstrap'

export const AddPost = (props) => {

    // const [title, setTitle] = useState("");
    // const [content, setContent] = useState("");


    // const addPost = (title, content) => {
    //     props.addPost(title);
    //      props.addPost(content);
    //     setTitle('');
    //     setContent('');
    // }
    const [state, setState] = useState({
        title: '',
        content: ''
    });


    const addPost = (evt) => {
        const value = evt.target.value;
        setState({
            ...state,
            [evt.target.state]: value
        }); setState('')
    }

    return (
        <Container>
            <Form>
                <h3>Add a New Post</h3>
                <Form.Label>Title</Form.Label>
                <Form.Control type="text" placeholder="Add a title" id="title"
                    value={state.title}
                    // onChange={(event) => setState((event.target.value))} />
                    onChange={addPost} />

                <Form.Group controlId="exampleForm.ControlTextarea1">
                    <Form.Label>Content</Form.Label>
                    <Form.Control as="textarea" rows={3} id="textarea"
                        value={state.content}
                        onChange={addPost} />
                </Form.Group>
                <Button type="submit" className='mt-3' onClick={() => addPost(state)}>Submit</Button>
            </Form>
        </Container>
    )
}
