import React, { useState, useEffect } from 'react';
import { useParams } from "react-router-dom";

import { Container, Form, Button } from 'react-bootstrap'


const EditPost = (props) => {



    // find the id from the url
    console.log(props);

    // const urlPath = window.location.pathname.split('/');
    // let id = myArray[myArray.length - 1];

    console.log(props.posts);
    // console.log(props.posts[0].id)
    // let id = props.posts[props.posts.length - 1];
    // console.log(id)

    // get the id from the url
    const urlPath = window.location.pathname.split('/');
    let id = urlPath[urlPath.length - 1];
    console.log(id)

    const post = props.posts.find(post => post.id == id);

    const updateHandel = (event) => {
        event.preventDefault();
        props.updatePost(post)
    }

    return (

        <Container className="container">
            <Form onChange={updateHandel}>
                <Form.Label className="label">Title</Form.Label>
                <Form.Control type="text" placeholder="Add a title" id="title"
                    defaultValue={post.title}
                    // onChange={(event) => setPost({ ...post, title: event.target.value })} />


                />
                <Form.Group controlId="exampleForm.ControlTextarea1">
                    <Form.Label className="label">Content</Form.Label>
                    <Form.Control as="textarea" rows={10} id="textarea"
                        defaultValue={post.content}
                    />
                </Form.Group>
                <Button type="submit" className='mt-3'>Update</Button>
            </Form>
        </Container>
    )
}

export default EditPost;
