import React,{useParams} from 'react';
import { Link } from 'react-router-dom';
import { Container, Row, Card, Button } from 'react-bootstrap';
import './PostList.css';

const PostList = (props) => {

    // const { id } = useParams()

    const deleteHandel = (id) => {
        props.deletePost(id)
    }

    return (
        <Container >
            <Row >
                <ul className='flex'>
                    {props.posts.map((element, index) =>
                        <li key={index}>
                            <Card style={{ margin: '1rem' }}>
                                <Card.Body>
                                    <Card.Title>{element.title}</Card.Title>
                                    <Card.Text>{element.content} </Card.Text>
                                    <Link to={`/post/${element.id}`}>
                                        <Button className='btn' variant="primary" >Read More</Button>
                                    </Link>
                                    <Button className='btn-edit'>Edit Post</Button>
                                    <Button className='btn-edit' onClick={() => deleteHandel(element.id)}>Delete Post</Button>

                                </Card.Body>
                            </Card>
                        </li>
                    )}
                </ul>
            </Row>
        </Container>
    )
}

export default PostList
