import axios from 'axios';
import React, { useState } from 'react';
import { Form, Button, Row } from 'react-bootstrap';

export const LogIn = () => {


    const [email, setEmail] = useState();
    const [password, setPassword] = useState();

    const [user, setUser] = useState();


    const addUser = async (user) => {
        axios.post('http://localhost:3001/users', { "user": user.email, "password": user.password }).then(() => setUser())
    }


    const submitHandel = (event) => {
            event.preventDefault();
            //         setEmail(event.target.value); no need 
            // setPassword(event.target.value);
            console.log(email);
            console.log(password);

        }
        return (
            <Form onSubmit={submitHandel} style={{ width: '600px', marginLeft: '100px' }}>
                <Form.Group className="mb-3" controlId="formBasicEmail">
                    <Form.Label>Email address</Form.Label>
                    <Form.Control value={email} type="email" placeholder="Enter email"
                        onChange={(event) => setEmail(event.target.value)}
                    />
                    <Form.Text className="text-muted">
                        We'll never share your email with anyone else.
          </Form.Text>
                </Form.Group>

                <Form.Group className="mb-3" controlId="formBasicPassword">
                    <Form.Label>Password</Form.Label>
                    <Form.Control value={password} type="password" placeholder="Password"
                        onChange={(event) => setPassword(event.target.value)}
                    />
                </Form.Group>
                <Form.Group className="mb-3" controlId="formBasicCheckbox">
                    <Form.Check type="checkbox" label="Check me out" />
                </Form.Group>
                <Button variant="primary" type="submit">
                    Submit
        </Button>
            </Form>
        )
    }
