import React, { useState } from 'react'
import { useHistory } from 'react-router-dom';
import { Container, Form, Button } from 'react-bootstrap'





export const AddPost = ({post: propsPost, addPost}) => {

    const [post, setPost] = useState({...propsPost});

    let history = useHistory()

    const handelForm = (event) => {
        event.preventDefault();
        if (post.title) {
            addPost(post);
            history.push('/')
        } else {
            alert ('title required');
        }
    }

    return (
        <Container>
            <Form onSubmit={handelForm}>
                <h3>Add a New Post</h3>
                <Form.Label>Title</Form.Label>
                <Form.Control type="text" placeholder="Add a title" id="title"
                    value={post.title}
                    onChange={(event) => setPost({ ...postevent.target.value })} />
                <Form.Group controlId="exampleForm.ControlTextarea1">
                    <Form.Label>Content</Form.Label>
                    <Form.Control as="textarea" rows={3} id="textarea"
                        value={content}
                        onChange={(event) => setContent(event.target.value)} />
                </Form.Group>
                <Button type="submit" className='mt-3'>Submit</Button>
            </Form>
        </Container>
    )
}
